import './App.css';
import React from 'react';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from "./components/ImageLinkForm/ImageLinkForm";
import Rank from "./components/Rank/Rank"; 
import ParticlesBg from 'particles-bg';
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js';

// This function returns the request options for the Clarifai API
const returnClarifaiJSONRequest = (imageUrl) => {
    // Your PAT (Personal Access Token) can be found in the portal under Authentification
    const PAT = 'a293b1da5a3a4e67a2636069fdea6ba7';
    // Specify the correct user_id/app_id pairings
    // Since you're making inferences outside your app's scope
    const USER_ID = 'clarifai';
    const APP_ID = 'main';  
    // Change these to whatever model and image URL you want to use
    const IMAGE_URL = 'https://samples.clarifai.com/metro-north.jpg';

    // setup the Json request
    const raw = JSON.stringify({
      "user_app_id": {
          "user_id": USER_ID,
          "app_id": APP_ID
      },
      "inputs": [
          {
              "data": {
                  "image": {
                      "url": IMAGE_URL
                      // "base64": IMAGE_BYTES_STRING
                  }
              }
          }
      ]    
  });

  const requestOptions = {
    method: 'POST',
    headers: {
        'Accept': 'application/json',
        'Authorization': 'Key ' + PAT
    },
    body: raw
  };
  return requestOptions;
}


class App extends React.Component {

  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
    }
  }

  calculateFaceLocations = (data) => {
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box; // Get the bounding box from the Clarifai API
    const image = document.getElementById('inputimage'); // Get the image from the DOM
    const width = Number(image.width); // Get the width of the image
    const height = Number(image.height); // Get the height of the image
    return { // Return an object containing the calculated values
      leftCol: clarifaiFace.left_col * width, // The left column of the bounding box
      topRow: clarifaiFace.top_row * height, // The top row of the bounding box
      rightCol: width - (clarifaiFace.right_col * width), // The right column of the bounding box
      bottomRow: height - (clarifaiFace.bottom_row * height) // The bottom row of the bounding box
    }

  }

  // This is a function that will be passed down to the ImageLinkForm component
  onInputChange = (event) => {
    console.log(event.target.value);
    this.setState({input: event.target.value});
  }

  onButtonSubmit = () => {
    console.log('click');
    this.setState({imageUrl: this.state.input});

    fetch("https://api.clarifai.com/v2/models/face-detection/outputs", returnClarifaiJSONRequest(this.state.input))
    .then(response => response.json())
    .then(result => {

        const regions = result.outputs[0].data.regions;

        regions.forEach(region => {
            // Accessing and rounding the bounding box values
            const boundingBox = region.region_info.bounding_box;
            const topRow = boundingBox.top_row.toFixed(3);
            const leftCol = boundingBox.left_col.toFixed(3);
            const bottomRow = boundingBox.bottom_row.toFixed(3);
            const rightCol = boundingBox.right_col.toFixed(3);

            region.data.concepts.forEach(concept => {
                // Accessing and rounding the concept value
                const name = concept.name;
                const value = concept.value.toFixed(4);

                console.log(`${name}: ${value} BBox: ${topRow}, ${leftCol}, ${bottomRow}, ${rightCol}`);


            });
        });

    })
    .catch(error => console.log('error', error));

  }

    render = () => {
      return (
        <div className="App">

        <ParticlesBg type="cobweb" bg={true} />
          <Navigation/>
          <Logo/>
          <Rank/>
          <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/> {/* Pass in The onInputChange event as a props*/}
          <FaceRecognition imageUrl={this.state.imageUrl}/>

        </div>
      );
      }
}

export default App;
