import './App.css';
import React from 'react';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from "./components/ImageLinkForm/ImageLinkForm";
import Rank from "./components/Rank/Rank"; 
import ParticlesBg from 'particles-bg';
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js';

// This function returns the request options for the Clarifai API
const returnClarifaiJSONRequest = (imageUrl) => {
    // Your PAT (Personal Access Token) can be found in the portal under Authentification
    const PAT = 'a293b1da5a3a4e67a2636069fdea6ba7';
    // Specify the correct user_id/app_id pairings
    // Since you're making inferences outside your app's scope
    const USER_ID = 'clarifai';
    const APP_ID = 'main';  
    // Change these to whatever model and image URL you want to use
    const IMAGE_URL = 'https://samples.clarifai.com/metro-north.jpg';

    // setup the Json request
    const raw = JSON.stringify({
      "user_app_id": {
          "user_id": USER_ID,
          "app_id": APP_ID
      },
      "inputs": [
          {
              "data": {
                  "image": {
                      "url": IMAGE_URL
                      // "base64": IMAGE_BYTES_STRING
                  }
              }
          }
      ]    
  });

  const requestOptions = {
    method: 'POST',
    headers: {
        'Accept': 'application/json',
        'Authorization': 'Key ' + PAT
    },
    body: raw
  };
  return requestOptions;
}


class App extends React.Component {

  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      boxes: [],
    }
  }

  // This is a function that will be passed down to the ImageLinkForm component
  onInputChange = (event) => {
    console.log(event.target.value);
    this.setState({input: event.target.value});
  }

  // Face API Bounding Box
  calculateFaceLocation = (data) => {
    console.log(data);
    const clarifaiFaces = data.outputs[0].data.regions;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    const boxes = clarifaiFaces.map(face => {
      const clarifaiFace = face.region_info.bounding_box;
      return {
        leftCol: clarifaiFace.left_col * width,
        topRow: clarifaiFace.top_row * height,
        rightCol: width - (clarifaiFace.right_col * width),
        bottomRow: height - (clarifaiFace.bottom_row * height),
      }
    });
    return boxes;
  }

  displayFaceBox = (boxes) => {
    this.setState({boxes: boxes});
  }

  onButtonSubmit = () => {
    console.log('click');
    this.setState({imageUrl: this.state.input});

    fetch("https://api.clarifai.com/v2/models/face-detection/outputs", returnClarifaiJSONRequest(this.state.input))
    .then(response => response.json())
    .then(result => 
      this.displayFaceBox(this.calculateFaceLocation(result)))
    .catch(error => console.log('error', error));

  }

    render = () => {
      return (
        <div className="App">

        <ParticlesBg type="cobweb" bg={true} />
          <Navigation/>
          <Logo/>
          <Rank/>
          <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/> {/* Pass in The onInputChange event as a props*/}
          <FaceRecognition boxes={this.state.boxes} imageUrl={this.state.imageUrl}/>

        </div>
      );
      }
}

export default App;
